import React,{useState} from "react";
import { Button, TextField, Card } from "@material-ui/core";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { useStyles } from "../../styles/styles";

import {Link } from "react-router-dom";


import {useDispatch, useSelector} from 'react-redux';
import {RegisterAction} from '../../redux/actions/AuthActions';




function RegisterComponent() {
  const classes = useStyles();

  const dispatch = useDispatch();

  //const authResponse = useSelector(state=>state.userAuth.authResponse);

  
  const [fields, setState] = useState({
    name: "",
    email: "",
    password: "",
    password_confirmation: "",
  });

  const handleFieldChange = e => {
      setState({
          ...fields,
          [e.target.id] : e.target.value
      })
  }

  const UserRegister = (e) => {
    e.preventDefault();
    console.log(fields);
    const passwordMatch = checkPasswordMatch(fields.password, fields.password_confirmation);

    if(passwordMatch === true){
      alert('As senhas devem ser iguais.');
      return;
    }
    dispatch(RegisterAction(fields));
  };

  

const checkPasswordMatch = (password,password_confirmation) => {
 return  password !== password_confirmation ? true : false; 
}
  return (
    <div>
      <div className={classes.centerItem}>
        <Card>
          <h2>
            <b>Cadastro</b>
          </h2>

           
          <form onSubmit={UserRegister}>
            <div>
              <TextField
                type="text"
                className={classes.fullWidth}
                required
                margin="normal"
                variant="outlined"
                label="name"
                id="name"
                value={fields.name}
                onChange={handleFieldChange}
              />
            </div>

            <div>
              <TextField
                type="email"
                className={classes.fullWidth}
                required
                margin="normal"
                variant="outlined"
                label="email"
                id="email"
                value={fields.email}
                onChange={handleFieldChange}
              />
            </div>

            <div>
              <div>
                <TextField
                  className={classes.fullWidth}
                  label="Password"
                  type="password"
                  margin="normal"
                  variant="outlined"
                  required
                  id="password"
                  value={fields.password}
                  onChange={handleFieldChange}
                />
              </div>

              <div>
                <TextField
                  className={classes.fullWidth}
                  label="Confirm Password"
                  type="password"
                  required
                  margin="normal"
                  variant="outlined"
                  id="password_confirmation"
                  value={fields.password_confirmation}
                  onChange={handleFieldChange}
                />
              </div>

              <div>
                <Button
                  type="submit"
                  className={classes.fullWidth}
                  variant="contained"
                  color="primary"
                  endIcon={<AccountCircleIcon />}
                >
                  <b>Register</b>
                </Button>
                <br />

                <div className={classes.linkContainer}>
                  <Link to="/user/login">Login Here</Link>
                </div>
              </div>

              <div className={classes.linkContainer}>
                <Link to="/home">Back To Home Page </Link>
              </div>

              <div></div>
            </div>
          </form>
        </Card>
      </div>
    </div>
  );
}

export default RegisterComponent;
